mapper.update:
update wms_ware_sku
set stock=stock + #{skuNum}
where sku_id = #{skuId}
  and ware_id = #{wareId};

update wms_ware_sku
set stock_locked=stock_locked + #{num}
where sku_id = #{skuId}
  and ware_id = #{wareId}
  and stock >= (stock_locked + #{num});

mapper.select:
/*查找有库存的仓库id*/
select ware_id
from wms_ware_sku
where sku_id = #{skuId}
  and stock > stock_locked
